import './Style.css';
import React from 'react';
import styled from "styled-components";
import Week from './Week';

class App extends React.Component{
  constructor(props){
    super(props);

    this.state = {
      weekarr: ["월","화","수","목","금","토","일"]
    };

    // this.text = React.createRef();
  }

  render() {
    return (
      <div className="App">
        <Container>
          <h3>내 일주일은?</h3>
          <Week weekarr={this.state.weekarr}/>
        </Container>

      </div>
    )
  }
}

const Container = styled.div`
  max-width: 350px;
  min-height: 80vh;
  background-color: #fff;
  padding: 16px;
  margin: 20px auto;
  border-radius: 5px;
  border: 1px solid #ddd;
  h3 {
    text-align: center;
  }
`;


export default App;
