import React from "react";
import styled from "styled-components";
import { useHistory, useParams } from "react-router";


const Detail = (props) => {
    console.log(props);
    const history = useHistory();
    const day = useParams();
    console.log(day.day);
     const [count] = React.useState(5);
    console.log(count)
    const circle_count = Array.from({length: count },(v,i)=> i);

    return ( 
       <div>
        <Title>
          <span>
           { day. day}요일
          </span>
          평점 남기기
        </Title>       
        {circle_count.map((n,i) => {
                return (
                <div key={i}>
                  <Circle/>
                </div>
                );
              })}
        <GoWeek onClick={() => {
            history.push("/");
            }}>
                평점 남기기
            </GoWeek>
        </div>
    );
}

const Title = styled.h3`
    span { 
        color: white;
        background-color: orange;
        padding: 0.5rem;
        border-radius: 5px;
    }
    text-align: center;
`

const Circle = styled.div`
  display: flex;
  width: 30px; 
  height: 30px; 
  border-radius: 30px; 
  margin: 5px; 
  background-color: #ddd;
  flexDirection: row;
`;

const GoWeek = styled.button`
    width: 100%;
    background-color: purple;
    border: none;
    border-radius: 5px;
    padding: 1rem;
    color: white;
`


export default Detail;
