import React from "react";
import styled from "styled-components";
import { useHistory, useParams } from "react-router";


const Detail = (props) => {
    console.log(props);
    const history = useHistory();
    const { id } = useParams();


    return ( 
       <div>
        <Title>
            <span>
            {params.week_day}요일
          </span>{" "}
          평점 남기기
        </Title>  

        <Circles>
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            margin: "1rem 0",
            width: "100%",
          }}

          {Array.from({ length: 5 }, (item, idx) => {
            return (
              <div
                key={index}
                onClick={() => {
                  setRate(index + 1);
                }}
                style={{
                  width: "30px",
                  height: "30px",
                  borderRadius: "30px",
                  margin: "5px",
                  backgroundColor: rate < idx + 1 ? "#ddd" : "#ffeb3b",
                }}
              ></div>
            );
          })}
        </Circles>

        <GoWeek onClick={() => {
            history.push("/");
            }}>
                평점 남기기
            </GoWeek>
        </div>
    );
}

const Title = styled.h3`
    span { 
        color: white;
        background-color: orange;
        padding: 0.5rem;
        border-radius: 5px;
    }
    text-align: center;
`

const GoWeek = styled.button`
    width: 100%;
    background-color: purple;
    border: none;
    border-radius: 5px;
    padding: 1rem;
    color: white;
`


export default Detail;
